mapper.select:
  	SELECT GROUP_CONCAT(from_date) AS fromDate, GROUP_CONCAT(to_date) AS toDate,create_time FROM holiday
  	where task_worker_id = #{taskWorkerId}
	GROUP BY DATE_FORMAT(create_time,'%y-%m-%d') order by create_time ASC

select create_time, modify_time, from_date, to_date
 from holiday where task_worker_id = #{taskWorkerId}
 order by create_time ASC
