mapper.select:
SELECT count(1) FROM inform


      and status = #{status}


      and send_type = #{sendType}


      and accept_type = #{acceptType}


        and receive_id = #{receiveId}

SELECT title,CASE WHEN DATE_FORMAT(create_time,'%Y-%m-%d') = CURDATE() THEN '今天' WHEN DATE_FORMAT(create_time,'%Y-%m-%d') = DATE_SUB(CURDATE(), INTERVAL 1 DAY) THEN '昨天' ELSE DATE_FORMAT(create_time,'%m-%d') END AS dateinfo,content, id AS pid, DATE_FORMAT(create_time,'%H:%i') AS timeinfo FROM inform
and send_type = #{sendType}
and accept_type = #{acceptType}
and receive_id = #{receiveId}
order by status asc,create_time desc
