mapper.select:
select

from message


        worker_id = #{workerId}


        and hotel_id = #{hotelId}


        and hr_company_id = #{hrCompanyId}


        and apply_type = #{applyType}

SELECT message_title AS messageTitle,CASE WHEN DATE_FORMAT(create_time,'%Y-%m-%d') = CURDATE() THEN '今天' WHEN DATE_FORMAT(create_time,'%Y-%m-%d') = DATE_SUB(CURDATE(), INTERVAL 1 DAY) THEN '昨天' ELSE DATE_FORMAT(create_time,'%m-%d') END AS days,message_content AS messageContent, id FROM message
and worker_id = #{workerId}
and hotel_id = #{hotelId}
and hr_company_id = #{hrCompanyId}
ORDER BY STATUS ASC ,create_time DESC

SELECT CASE message_type WHEN 1 THEN '补签申请' WHEN 2 THEN '加时申请' WHEN 3 THEN '请假申请'
WHEN 4 THEN '调配申请' WHEN 5 THEN '绑定申请' WHEN 6 THEN '任务推荐' END AS messageCode,create_time,message_content FROM message
WHERE STATUS = 0 AND deleted = 0

    and worker_id = #{workerId}


    and hotel_id = #{hotelId}


    and hr_company_id = #{hrCompanyId}



        and apply_type in (0, 1)


        and apply_type = #{applyType}

SELECT COUNT(1) FROM message WHERE deleted = 0

      and status = #{status}


      and worker_id = #{workerId}


      and hotel_id = #{hotelId}


      and hr_company_id = #{hrCompanyId}



          and apply_type in (0, 1)


          and apply_type = #{applyType}



  and message_code = #{messageCode}


  and applicant_type = #{applicantType}


    and message_type = #{messageType}

select message_code, message_content, message_link, message_title, msg_link, content from
message


        and worker_id = #{workerId}


        and hotel_id = #{hotelId}


        and hr_company_id = #{hrCompanyId}


        and applicant_type = #{applicantType}


        and apply_type = #{applyType}

    order by create_time desc

SELECT m.is_task,m.content,m.id AS messageId,m.create_time,IFNULL(c.address,'') AS address,c.logo,t.task_type_text,t.fromDate,t.toDate,t.dayStartTime , message_type AS TYPE, IFNULL(t.hourly_pay,0) AS hourlyPay ,t.dayEndTime,t.hotel_name,IFNULL(tc.confirmed_workers,0) AS confirmedWorkers,IFNULL(tc.need_workers,0) AS needWorkers,IFNULL(tc.need_workers,0)-IFNULL(tc.confirmed_workers,0) AS lackWorkers,t.hotel_id,t.id FROM message m INNER JOIN ( SELECT task_type_text,DATE_FORMAT(from_date,'%Y.%m.%d') AS fromDate,DATE_FORMAT(to_date, '%Y.%m.%d') AS toDate,DATE_FORMAT(day_start_time, '%H:%i') AS dayStartTime,DATE_FORMAT(day_end_time, '%H:%i') AS dayEndTime,hotel_name,confirmed_workers,need_workers,hotel_id,id,hourly_pay FROM task ) t ON m.task_id = t.id INNER JOIN company c ON c.id = t.hotel_id INNER JOIN task_hr_company tc ON tc.id = m.hr_task_id WHERE m.apply_type in (0,1) AND m.is_task = 0 AND m.status = 0 AND m.worker_id= #{workerId}
UNION
( SELECT m2.is_task,m2.content,m2.id AS messageId,m2.`create_time`,IFNULL(c2.address,'') AS address,c2.logo,NULL AS task_type_text,NULL AS from_date, NULL AS to_date,NULL AS day_start_time,NULL AS day_end_time, NULL AS hotel_name, 0 AS confirmed_workers,0 AS need_workers, 0 AS lackWorkers,NULL AS hotel_id, NULL AS id, 5 AS TYPE, 0 AS hourlyPay FROM message m2 INNER JOIN company c2 ON (m2.hotel_id = c2.id OR m2.`hr_company_id` = c2.`id`) WHERE m2.apply_type in (0,1) AND m2.is_task = 1 AND m2.status = 0 AND worker_id= #{workerId} ) ORDER BY create_time

SELECT m.is_task,m.content,m.id AS messageId,m.create_time,IFNULL(c.address,''),c.logo,t.task_type_text,DATE_FORMAT(t.from_date, '%Y.%m.%d') as fromDate,DATE_FORMAT(t.to_date, '%Y.%m.%d') as toDate,DATE_FORMAT(t.day_start_time, '%H:%i') as dayStartTime,DATE_FORMAT(t.day_end_time, '%H:%i') as dayEndTime,t.hotel_name,IFNULL(t.confirmed_workers,0) as confirmedWorkers,IFNULL(t.need_workers,0) as needWorkers, t.lackWorkers,t.hotel_id,t.id,message_type AS type,IFNULL(t.hourly_pay,0) as hourlyPay,c.area FROM message m INNER JOIN ( SELECT task_type_text,from_date,to_date,day_start_time,day_end_time,hotel_name,confirmed_workers,need_workers,IFNULL(need_workers,0)-IFNULL(confirmed_workers,0) AS lackWorkers,hotel_id,id,hourly_pay FROM task ) t ON m.task_id = t.id INNER JOIN company c ON c.id = t.hotel_id WHERE m.apply_type=3 AND m.is_task = 0 and m.status = 0 AND m.hotel_id= #{hotelId}
UNION
( SELECT m3.is_task,m3.content,m3.id AS messageId,m3.`create_time`,IFNULL(c3.`address`,'') as address,c3.`logo`,NULL AS task_type_text,NULL AS from_date, NULL AS to_date,NULL AS day_start_time,NULL AS day_end_time, NULL AS hotel_name, 0 AS confirmed_workers,0 AS need_workers, 0 AS lackWorkers,NULL AS hotel_id, NULL AS id, message_type AS TYPE,0 AS hourlyPay,c3.`area` FROM message m3 INNER JOIN company c3 ON c3.id = m3.`hr_company_id` WHERE m3.apply_type=3 AND m3.is_task = 1 and m3.status = 0 AND m3.hotel_id= #{hotelId} ) ORDER BY create_time DESC

      SELECT u.`nickname` as name,IFNULL(u.age,0) as age,u.`mobile`,u.`sex`,m.message_type,u.avatar as logo,m.content,u.`worker_id` as pid,DATE_FORMAT(m.create_time,'%Y/%m/%d %H:%i:%s') as createTime,m.worker_task_id as workerTaskId,m.hr_task_id as taskHrId,m.task_id AS taskHotelId FROM
(
SELECT * FROM message WHERE id = #{messageId}
) m
INNER JOIN USER u
ON u.`worker_id` = m.`worker_id`

        SELECT u.`nickname` AS name,IFNULL(u.age,0) as age,u.`mobile`,u.`sex`,m.message_type,u.avatar AS logo,m.content,DATE_FORMAT(m.supplement_time,'%Y.%m.%d') AS overtimeDate
,u.`worker_id` as pid,DATE_FORMAT(m.create_time,'%Y/%m/%d %H:%i:%s') AS createTime,m.minutes AS overtimeLength,m.hr_task_id as taskHrId,m.worker_task_id as workerTaskId  FROM
(
SELECT * FROM message WHERE id = #{messageId}
) m INNER JOIN USER u
ON u.`worker_id` = m.`worker_id`

        SELECT u.`nickname` AS name,IFNULL(u.age,0) as age,u.`mobile`,u.`sex`,m.message_type,u.avatar AS logo,m.content,DATE_FORMAT(m.supplement_time,'%Y.%m.%d') AS leaveStartDate
,DATE_FORMAT(m.supplement_time_end,'%Y.%m.%d') AS leaveEndDate, DATE_FORMAT(m.supplement_time,'%H:%i') AS leaveStartTime,DATE_FORMAT(m.supplement_time_end,'%H:%i') AS leaveEndTime ,u.`worker_id` as pid,DATE_FORMAT(m.create_time,'%Y/%m/%d %H:%i:%s') AS createTime,m.hr_task_id as taskHrId,m.worker_task_id as workerTaskId  FROM
(
SELECT * FROM message WHERE id = #{messageId}
) m INNER JOIN USER u
ON u.`worker_id` = m.`worker_id`
