mapper.select:
select

from message


        worker_id = #{workerId}


        and hotel_id = #{hotelId}


        and hr_company_id = #{hrCompanyId}


        and apply_type = #{applyType}

SELECT COUNT(1) FROM message WHERE deleted = 0

      and status = #{status}


      and worker_id = #{workerId}


      and hotel_id = #{hotelId}


      and hr_company_id = #{hrCompanyId}



          and apply_type in (0, 1)


          and apply_type = #{applyType}



  and message_code = #{messageCode}


  and applicant_type = #{applicantType}


    and message_type = #{messageType}

select message_code, message_content, message_link, message_title, msg_link, content from
message


        and worker_id = #{workerId}


        and hotel_id = #{hotelId}


        and hr_company_id = #{hrCompanyId}


        and applicant_type = #{applicantType}


        and apply_type = #{applyType}

    order by create_time desc

SELECT m.is_task,m.content,m.id AS messageId,m.create_time AS createTime,IFNULL(c.address,'') AS address,c.logo,t.task_type_text,t.fromDate,t.toDate,t.dayStartTime,t.dayEndTime ,t.hotel_name,IFNULL(th.confirmed_workers,0) AS confirmedWorkers,IFNULL(th.need_workers,0) AS needWorkers,t.task_content AS taskContent, IFNULL(th.need_workers,0)-IFNULL(th.confirmed_workers,0) AS lackWorkers,m.`hotel_id` AS hotelId,t.id ,m.applicant_type as applicantType,m.apply_type as applyType,message_type AS type,t.hourlyPay,c.area FROM message m INNER JOIN ( SELECT task_type_text,DATE_FORMAT(from_date,'%Y.%m.%d') AS fromDate,task_content, DATE_FORMAT(to_date, '%Y.%m.%d') AS toDate,DATE_FORMAT(day_start_time, '%H:%i') AS dayStartTime, DATE_FORMAT(day_end_time, '%H:%i') AS dayEndTime,hotel_name,hotel_id,id,IFNULL(hourly_pay,0.0) AS hourlyPay FROM task ) t ON m.task_id = t.id INNER JOIN company c ON c.id = t.hotel_id INNER JOIN task_hr_company th ON th.id = m.`hr_task_id` WHERE m.apply_type=2 and ((th.from_date > NOW() AND m.is_stop = 0) OR m.is_stop = 1) AND m.is_task = 0 AND m.status = 0 AND m.message_type IN (6,10,11) AND m.hr_company_id= #{hrId}
union
( SELECT m.is_task,m.content,m.id AS messageId,m.create_time AS createTime,IFNULL(c.address,'') AS address,c.logo,t.task_type_text,t.fromDate,t.toDate,t.dayStartTime,t.dayEndTime ,t.hotel_name,IFNULL(th.confirmed_workers,0) AS confirmedWorkers,IFNULL(th.need_workers,0) AS needWorkers,t.task_content AS taskContent, IFNULL(th.need_workers,0)-IFNULL(th.confirmed_workers,0) AS lackWorkers,m.`hotel_id` AS hotelId,t.id ,m.applicant_type as applicantType,m.apply_type as applyType,message_type AS type,t.hourlyPay,c.area FROM message m INNER JOIN ( SELECT task_type_text,DATE_FORMAT(from_date,'%Y.%m.%d') AS fromDate,task_content, DATE_FORMAT(to_date, '%Y.%m.%d') AS toDate,DATE_FORMAT(day_start_time, '%H:%i') AS dayStartTime, DATE_FORMAT(day_end_time, '%H:%i') AS dayEndTime,hotel_name,hotel_id,id,IFNULL(hourly_pay,0.0) AS hourlyPay FROM task) t ON m.task_id = t.id INNER JOIN company c ON c.id = t.hotel_id INNER JOIN task_hr_company th ON th.id = m.`hr_task_id` WHERE m.apply_type=2 AND m.is_task = 0 AND m.status = 0 AND m.message_type IN (4,7,8,9) AND m.hr_company_id= #{hrId} )
UNION ( SELECT m2.is_task,m2.message_content as content,m2.id AS messageId,m2.`create_time` AS createTime,NULL AS address,u.avatar AS logo,NULL AS task_type_text,NULL AS fromDate, NULL AS toDate,NULL AS dayStartTime,NULL AS dayEndTime, NULL AS hotel_name, 0 AS confirmedWorkers,0 AS needWorkers,NULL AS taskContent, 0 AS lackWorkers, NULL AS hotelId, NULL AS id,m2.applicant_type as applicantType,m2.apply_type as applyType,message_type AS type,0 AS hourlyPay, NULL AS AREA FROM message m2 INNER JOIN USER u ON u.`worker_id` = m2.`worker_id` WHERE m2.apply_type=2 AND m2.is_task = 1 AND m2.status = 0 AND m2.hr_company_id= #{hrId}
) UNION ( SELECT m3.is_task,m3.message_content as content,m3.id AS messageId,m3.`create_time` AS createTime,NULL AS address,c3.`logo`,NULL AS task_type_text,NULL AS fromDate, NULL AS toDate,NULL AS dayStartTime,NULL AS dayEndTime,NULL AS hotel_name, 0 AS confirmedWorkers,0 AS needWorkers,NULL AS taskContent, 0 AS lackWorkers,NULL AS hotelId, NULL AS id, m3.applicant_type as applicantType,m3.apply_type as applyType,message_type AS type,0 AS hourlyPay,c3.area FROM message m3 INNER JOIN company c3 ON c3.id = m3.`hotel_id` WHERE m3.apply_type=2 AND m3.is_task = 1 AND m3.status = 0 AND m3.applicant_type = 3 AND m3.hr_company_id= #{hrId}
) ORDER BY createTime DESC

