      SELECT u.`nickname` as name,IFNULL(u.age,0) as age,u.`mobile`,u.`sex`,m.message_type,u.avatar as logo,m.content,u.`worker_id` as pid,DATE_FORMAT(m.create_time,'%Y/%m/%d %H:%i:%s') as createTime,m.worker_task_id as workerTaskId,m.hr_task_id as taskHrId,m.task_id AS taskHotelId FROM
(
SELECT * FROM message WHERE id = #{messageId}
) m
INNER JOIN USER u
ON u.`worker_id` = m.`worker_id`

        SELECT tw.type as type,DATE_FORMAT(t.from_date,'%Y.%m.%d') AS fromDate, DATE_FORMAT(t.to_date,'%Y.%m.%d') AS toDate,DATE_FORMAT(t.day_start_time,'%H:%i') AS dayStartTime,DATE_FORMAT(t.day_end_time,'%H:%i') AS dayEndTime,t.task_content,c.id AS hotelId,c.area,

c.name,c.address,c.leader,c.leader_mobile,m.message_type,t.id AS taskId,m.id AS messageId,t.need_workers AS needWorkers,t.confirmed_workers AS confirmedWorkers,
k.hrName,k.hrId,k.hrLeader,IFNULL(k.hourly_pay,'') AS hourlyPay,t.task_type_text as messageTitle,k.hrLeaderMobile,k.messageCode,m.task_id AS taskHotelId,m.hr_task_id AS taskHrId,

m.worker_task_id AS workerTaskId,t.hourly_pay AS hourlyPayHotel,t.`task_type_code` AS taskTypeCode,t.task_type_text as taskTypeText,IFNULL(k.settlementPeriod,t.worker_settlement_period) AS workerSettlementPeriod,IFNULL(k.settlementNum,t.worker_settlement_num) AS workerSettlementNum,t.settlement_num,t.settlement_period
  FROM (SELECT * FROM message WHERE id = #{messageId}

            AND applicant_type = #{type}

        ) m INNER JOIN task t ON m.task_id = t.id
INNER JOIN company c ON t.hotel_id = c.id

LEFT JOIN
(
SELECT tc.need_workers,confirmed_workers,c2.name AS hrName,c2.id AS hrId,c2.leader AS hrLeader,
tc.hourly_pay,tc.task_type_text AS messageTitle,tc.worker_settlement_period as settlementPeriod,tc.worker_settlement_num as settlementNum
,c2.leader_mobile AS hrLeaderMobile,m2.id AS id,tc.task_type_code AS messageCode FROM (SELECT * FROM message WHERE id = #{messageId}

    AND applicant_type = #{type}

) m2 INNER JOIN
task_hr_company tc ON m2.hr_task_id = tc.id INNER JOIN company c2
ON c2.id = m2.hr_company_id
) k ON k.id = m.id
left JOIN task_worker tw ON m.worker_task_id = tw.id and tw.status = 0

