mapper.update:
update work_log


        modify_time = #{modifyTime},


        from_date = #{fromDate},


        to_date = #{toDate}


        punch_date = #{punchDate}


        minutes = #{minutes}


where id = #{id}

update work_log
set status =

    when #{item.pid} then #{item.status}

where id in

    #{item.pid}

mapper.select:
SELECT group_concat(from_date) as fromDate,group_concat(to_date) as toDate,GROUP_CONCAT(IFNULL(STATUS,0)) AS status,id, GROUP_CONCAT(IFNULL(employer_confirm_status,0)) AS employerConfirmStatus,create_time as createTime
FROM work_log WHERE task_worker_id = #{taskWorkerId}
GROUP BY DATE_FORMAT(create_time, '%y-%m-%d') order by create_time ASC
